library(httr)
library(jsonlite)

source("config/api.R")

test_URI <-
  "data:image/png;base64,"
DEBUG_MODE <- FALSE

fetchImage <- function(station, timestamp = "closest") {
  if (DEBUG_MODE) {
    URI <- test_URI
  } else {
    URI <- NULL
  }
  if (timestamp == "closest") {
    # return the up-to-date image for camera station
    # TODO: ask Dave for API to fetch nearest timestamp
    timestamp <- "2021-05-01-06-21-10"
    request <-
      paste0(
        imgret_base,
        imgret_endpoint,
        '?',
        'filename',
        '=',
        timestamp,
        '-',
        station,
        '.jpg'
      )
    response <- GET(request)
    if (DEBUG_MODE) {
      showLog()
      logjs(station)
      logjs(request)
      # logjs(response)
      # logjs(content(response)$body)
    }
    cont <- content(response)
    if (cont$statusCode == 200) {
      if (cont$isBase64Encoded) {
        if (DEBUG_MODE) {
          logjs("I got here")
        }
        URI = paste0("data:image/png;base64,", cont$body)
      }
    } else {
      URI = NULL
    }
  }
  return(URI)
}
